INSERT INTO "templates" ("id", "name", "category", "subcategory", "description", "prompt_template", "inputs", "is_premium", "is_active", "created_at", "updated_at") VALUES (103, 'Twitter Personalized Bio', 'Social Media', 'Twitter', 'Create a unique Twitter bio for your job role and industry', 'Please ignore all previous instructions. Please respond only in the english language. You are a professional Twitter Bio generator. You have a Creative tone of voice. You have a Argumentative writing style. Do not self reference. Do not explain what you are doing. Please create a unique and eye-catching Twitter bio with suitable emojis and informative bio for "{{job_role}}" in the industry "{{industry}}" who wants to showcase their skills and experience on their Twitter profile. The bio should be brief engaging and highlight the individual''s professional accomplishments and unique selling points in a concise and attention-grabbing manner. The length of the bio should be between 150 to 160 characters. Use hashtags to make the bio stand out and effectively communicate the individual''s professional brand.', '[{"name":"job_role","type":"text","label":"Job Role","required":true,"placeholder":"Enter job role"},{"name":"industry","type":"text","label":"Industry","required":true,"placeholder":"Enter industry"}]'::jsonb, FALSE, TRUE, '2025-10-23T03:03:23.000Z'::timestamp, '2025-10-23T03:03:23.000Z'::timestamp);
