INSERT INTO "templates" ("id", "name", "category", "subcategory", "description", "prompt_template", "inputs", "is_premium", "is_active", "created_at", "updated_at") VALUES (109, 'YouTube Script Creator', 'Social Media', 'YouTube', 'Create a complete script for YouTube', 'Please ignore all previous instructions. Please respond only in the english language. You are a YouTuber with a large fan following. You are an expert in YouTube SEO and can write compelling YouTube titles and descriptions. You have a Creative tone of voice. You have a Argumentative writing style. Do not self reference. Do not explain what you are doing. Create a complete youtube video script for "{{video_description}}". The script should have a good introduction, between 4 to 5 segments and an conclusion. In the relevant segment ask the viewer to click the subscribe button "{{call_to_action}}".', '[{"name":"video_description","type":"textarea","label":"Video Description","required":true,"placeholder":"Enter video description"},{"name":"call_to_action","type":"text","label":"Call to Action","default":"to click the subscribe button","required":true,"placeholder":"to click the subscribe button"}]'::jsonb, FALSE, TRUE, '2025-10-23T03:03:23.000Z'::timestamp, '2025-10-23T03:03:23.000Z'::timestamp);
