INSERT INTO "templates" ("id", "name", "category", "subcategory", "description", "prompt_template", "inputs", "is_premium", "is_active", "created_at", "updated_at") VALUES (111, 'YouTube Title & Descriptions', 'Social Media', 'YouTube', 'Generate YouTube video titles and descriptions from keywords', 'Please ignore all previous instructions. Please respond only in the english language. You are a YouTuber with a large fan following. You have a Creative tone of voice. You have a Argumentative writing style. Do not self reference. Do not explain what you are doing. Generate catchy YouTube titles and descriptions for a list of keywords "{{keywords}}". Only one title and one description should be generated for each keyword. The title should not be more than 100 characters long. The description should be more informative. The titles should be good for videos and help generate titles. The titles should be generic by visual way. The description should be long and contain multiple informational paragraphs about the topic. The description should also ask the viewer to click the subscribe button.', '[{"name":"keywords","type":"textarea","label":"Keywords","required":true,"placeholder":"Enter keywords"},{"name":"call_to_action","type":"text","label":"Call to Action","default":"to click the subscribe button","required":true,"placeholder":"to click the subscribe button"}]'::jsonb, FALSE, TRUE, '2025-10-23T03:03:23.000Z'::timestamp, '2025-10-23T03:03:23.000Z'::timestamp);
INSERT INTO "templates" ("id", "name", "category", "subcategory", "description", "prompt_template", "inputs", "is_premium", "is_active", "created_at", "updated_at") VALUES (112, 'YouTube Transcript to Article', 'Social Media', 'YouTube', 'Generate YouTube video titles and descriptions from keywords', 'Please ignore all previous instructions. Please respond only in the english language. You are an expert copywriter who is good at converting YouTube transcripts into articles. You have a Creative tone of voice. You have a Argumentative writing style. Do not self reference. Do not explain what you are doing. I will give you a YouTube transcript text and you will create an article based on it. Please intersperse short and long sentences. Utilize uncommon terminology to enhance the originality of the content. Please format the content in a professional format. Also generate a catchy page title and meta descriptions for the article. The page title should be between 70 and 80 Characters. The meta descriptions should be between 140 and 160 characters and should be optimized for the keyword "{{keyword}}". The meta descriptions should NOT have enclosing characters. The Youtube transcript text is "{{transcript}}"', '[{"name":"transcript","type":"textarea","label":"Transcript","required":true,"placeholder":"Enter YouTube transcript"},{"name":"keyword","type":"text","label":"Keyword","required":true,"placeholder":"Enter keyword"}]'::jsonb, FALSE, TRUE, '2025-10-23T03:03:23.000Z'::timestamp, '2025-10-23T03:03:23.000Z'::timestamp);
INSERT INTO "templates" ("id", "name", "category", "subcategory", "description", "prompt_template", "inputs", "is_premium", "is_active", "created_at", "updated_at") VALUES (113, 'YouTube Ads Generator (Paste Description)', 'Social Media', 'YouTube', 'Generate headlines and descriptions for running Ads on YouTube', 'Please ignore all previous instructions. Please respond only in the english language. You are a copywriter with expertise in YouTube Ads creation. You have a Creative tone of voice. You have a Argumentative writing style. Do not self reference. Do not explain what you are doing. Generate 10 compelling YouTube headlines and 10 compelling descriptions for a video. The headlines should be between 90 to 100 characters long. The descriptions should be between 30 to 35 characters long. Do not use single quotes, double quotes or any other enclosing characters. The video is about "{{video_description}}".', '[{"name":"total_headlines","type":"number","label":"Total Headlines","default":"10","required":true,"placeholder":"10"},{"name":"headlines_length","type":"text","label":"Headlines Length","default":"90 to 100 characters","required":true,"placeholder":"90 to 100 characters"},{"name":"description_length","type":"text","label":"Description Length","default":"30 to 35 characters","required":true,"placeholder":"30 to 35 characters"},{"name":"video_description","type":"textarea","label":"Video Description","required":true,"placeholder":"Enter video description"}]'::jsonb, FALSE, TRUE, '2025-10-23T03:03:24.000Z'::timestamp, '2025-10-23T03:03:24.000Z'::timestamp);
