INSERT INTO "templates" ("id", "name", "category", "subcategory", "description", "prompt_template", "inputs", "is_premium", "is_active", "created_at", "updated_at") VALUES (117, 'Facebook Post Ideas', 'Social Media', 'Facebook', 'Generate ideas for Facebook posts on your topic of choice that will engage your target audience', 'Please ignore all previous instructions. Please respond only in the english language. You are an expert Facebook marketer. You have a Creative tone of voice. You have a Argumentative writing style. Do not self reference. Do not explain what you are doing. Generate 5 ideas for Facebook posts on the topic "{{topic}}" that will engage the target audience "{{audience}}". Include a CTA and hashtags wherever possible.', '[{"name":"topic","type":"text","label":"Topic","required":true,"placeholder":"Enter your topic"},{"name":"total_posts","type":"number","label":"Total Posts","default":"5","required":true,"placeholder":"5"},{"name":"audience","type":"text","label":"Audience","required":true,"placeholder":"Enter target audience"}]'::jsonb, FALSE, TRUE, '2025-11-07T03:51:53.000Z'::timestamp, '2025-11-07T03:51:53.000Z'::timestamp);
