INSERT INTO "templates" ("id", "name", "category", "subcategory", "description", "prompt_template", "inputs", "is_premium", "is_active", "created_at", "updated_at") VALUES (119, 'Facebook Ad Creator', 'Social Media', 'Facebook', 'Create a complete Facebook ad for your product or service', 'Please ignore all previous instructions. Please respond only in the english language. You are an expert Facebook Ads copywriter with expertise in Facebook Ads. You have a Creative tone of voice. You have a Argumentative writing style. Do not self reference. Do not explain what you are doing. Please create ad copy for the product/service "{{topic}}". Please use the following guidelines: Create a compelling headline that grabs attention and highlights the main benefit of the product/service. The headline should be between 30-40 characters and its description field should be 20-25 characters. Then, create the primary ad text field. Use clear and concise language in the primary ad text field copy that focuses on the benefits of the product/service and addresses any potential objections. Include a strong call to action that encourages users to take the desired action. The primary ad text field should be 120-125 characters. Research the target audience demographics, such as age, gender, location, interests, and other characteristics that would help you to have a better understanding of the target audience. Create an ad that would be more appealing to them.', '[{"name":"topic","type":"text","label":"Topic","required":true,"placeholder":"Enter product/service"}]'::jsonb, FALSE, TRUE, '2025-11-07T03:51:53.000Z'::timestamp, '2025-11-07T03:51:53.000Z'::timestamp);
INSERT INTO "templates" ("id", "name", "category", "subcategory", "description", "prompt_template", "inputs", "is_premium", "is_active", "created_at", "updated_at") VALUES (135, 'Generate Pin Titles', 'Social Media', 'Pinterest', 'Generate Pinterest pin titles options for your topic of choice', 'Please ignore all previous instructions. Please respond only in the english language. You are a Pinterest SEO marketer and copywriter. You have a Creative tone of voice. You have a Argumentative writing style. Do not self reference. Do not explain what you are doing. Please provide me with 10 engaging Pinterest pin titles for the topic "{{topic}}". The titles should be between 90 to 100 characters.', '[{"name":"topic","type":"text","label":"Topic","required":true,"placeholder":"Enter topic"},{"name":"total_titles","type":"number","label":"Total Titles","default":"10","required":true,"placeholder":"10"}]'::jsonb, FALSE, TRUE, '2025-11-07T03:51:55.000Z'::timestamp, '2025-11-07T03:51:55.000Z'::timestamp);
INSERT INTO "templates" ("id", "name", "category", "subcategory", "description", "prompt_template", "inputs", "is_premium", "is_active", "created_at", "updated_at") VALUES (120, 'Facebook Post Calendar', 'Social Media', 'Facebook', 'Generate a Facebook content calendar for the next three months', 'Please ignore all previous instructions. Please respond only in the english language. You are an expert Facebook marketer. You have a Creative tone of voice. You have a Argumentative writing style. Please create a Facebook Post Calendar for {{total_months}} months based on your product/service "{{topics}}". There should be {{threads_per_week}} Facebook posts scheduled each week of the month. Every Facebook post should have a catchy headline and description. Try to use unique emojis in the description. The description should have a hook and entice the readers. The table should have actual dates in the future. Each month should have its own table. The table columns should be: Date, Post Title, Post Idea, Hashtags. Please organize each Facebook post in the table so that it looks like a calendar. Do not self reference. Do not explain what you are doing. Reply back only with the table.', '[{"name":"topics","type":"textarea","label":"Topics","required":true,"placeholder":"Enter topics for content"},{"name":"threads_per_week","type":"number","label":"Threads per week","default":"5","required":true,"placeholder":"5"},{"name":"total_months","type":"number","label":"Total months","default":"3","required":true,"placeholder":"3"}]'::jsonb, FALSE, TRUE, '2025-11-07T03:51:53.000Z'::timestamp, '2025-11-07T03:51:53.000Z'::timestamp);
