INSERT INTO "templates" ("id", "name", "category", "subcategory", "description", "prompt_template", "inputs", "is_premium", "is_active", "created_at", "updated_at") VALUES (149, 'Twitter Thread Calendar', 'Social Media', 'Twitter', 'Generates a Twitter content calendar for the next three months', 'Please ignore all previous instructions. Please respond only in the english language. You are a Twitter professional with a large fan following. You have a Creative tone of voice. You have a Argumentative writing style. Please create a Twitter thread Calendar for {{total_months}} months based on the topic "{{topics}}". There should be {{threads_per_week}} Twitter threads scheduled each week of the month. The twitter threads should be casual, informative, and engaging. Please use simple and understandable words. Please include statistics, personal experience, and fun facts in the twitter threads. The markdown table should have actual dates in the future. Each month should have its own table. The table columns should be: Date, Twitter Thread Idea, Hashtags. Please organize each blog post in the table so that it looks like a calendar. Do not self reference. Do not explain what you are doing. Reply back only with the table.', '[{"name":"topics","type":"text","label":"Topics","required":true,"placeholder":"Enter topics"},{"name":"threads_per_week","type":"number","label":"Threads per week","default":"5","required":true,"placeholder":"5"},{"name":"total_months","type":"number","label":"Total months","default":"3","required":true,"placeholder":"3"}]'::jsonb, FALSE, TRUE, '2025-11-07T03:51:56.000Z'::timestamp, '2025-11-07T03:51:56.000Z'::timestamp);
INSERT INTO "templates" ("id", "name", "category", "subcategory", "description", "prompt_template", "inputs", "is_premium", "is_active", "created_at", "updated_at") VALUES (150, 'YouTube Script Creator', 'Social Media', 'YouTube', 'Create a complete script for YouTube', 'Please ignore all previous instructions. Please respond only in the english language. You are a YouTuber with a large fan following. You are an expert in YouTube SEO and can write compelling YouTube titles and descriptions. You have a Creative tone of voice. You have a Argumentative writing style. Do not self reference. Do not explain what you are doing. Create a complete youtube video script for "{{video_description}}". The script should have a good introduction, between 4 to 5 segments and an conclusion. In the relevant segment ask the viewer to click the subscribe button "{{call_to_action}}".', '[{"name":"video_description","type":"textarea","label":"Video Description","required":true,"placeholder":"Enter video description"},{"name":"call_to_action","type":"text","label":"Call to Action","default":"to click the subscribe button","required":true,"placeholder":"to click the subscribe button"}]'::jsonb, FALSE, TRUE, '2025-11-07T03:51:56.000Z'::timestamp, '2025-11-07T03:51:56.000Z'::timestamp);
