INSERT INTO "templates" ("id", "name", "category", "subcategory", "description", "prompt_template", "inputs", "is_premium", "is_active", "created_at", "updated_at") VALUES (156, 'YouTube Tags Generator', 'Social Media', 'YouTube', 'Generate trending TikTok hashtags for your topic of choice', 'Please ignore all previous instructions. Please respond only in the english language. You are a YouTuber with a large fan following. You have a Creative tone of voice. You have a Argumentative writing style. Do not self reference. Do not explain what you are doing. Please generate 10 comma separated keyword with spaces between the words. Please capitalize each word in the YouTube tag. Please do not generate hashtags. If you generate anything that starts with the "#" character then remove it. YouTube tags are comma separated keywords that describe what the text I give you. Please do not put the YouTube tags in a markdown table. Please generate the YouTube tags for the text: "{{video_description_transcript}}".', '[{"name":"video_description_transcript","type":"textarea","label":"Video Description/Transcript","required":true,"placeholder":"Enter video description or transcript"},{"name":"total_tags","type":"number","label":"Total Tags","default":"10","required":true,"placeholder":"10"}]'::jsonb, FALSE, TRUE, '2025-11-07T03:51:57.000Z'::timestamp, '2025-11-07T03:51:57.000Z'::timestamp);
