INSERT INTO "templates" ("id", "name", "category", "subcategory", "description", "prompt_template", "inputs", "is_premium", "is_active", "created_at", "updated_at") VALUES (167, 'Instagram Reels Script Writer', 'Social Media', 'Instagram', 'Write an engaging Instagram Reels video script for your topic and target audience of choice', 'Please ignore all previous instructions. Please respond only in the english language. You are an Instagram marketer and influencer. You have a Creative tone of voice. You have a Argumentative writing style. Do not self reference. Do not explain what you are doing. Please write me a Instagram Reels video script for the topic "{{topic}}". The target audience is "{{audience}}". The length of the video should be less than 90 seconds long. The script needs to have a catchy title, follow the best practices of Instagram Reels, and get as much traction from the target audience as possible. Please include relevant hashtags to be used.', '[{"name":"topic","type":"text","label":"Topic","required":true,"placeholder":"Enter topic"},{"name":"audience","type":"text","label":"Audience","required":true,"placeholder":"Enter target audience"}]'::jsonb, FALSE, TRUE, '2025-11-07T03:59:36.000Z'::timestamp, '2025-11-07T03:59:36.000Z'::timestamp);
INSERT INTO "templates" ("id", "name", "category", "subcategory", "description", "prompt_template", "inputs", "is_premium", "is_active", "created_at", "updated_at") VALUES (168, 'LinkedIn Post Creator', 'Social Media', 'LinkedIn', 'Create engaging content post for LinkedIn', 'Please ignore all previous instructions. Please respond only in the english language. You are a LinkedIn content creator. You have a Creative tone of voice. You have a Argumentative writing style. Do not self reference. Do not explain what you are doing. Your content should be engaging, informative, and relevant to LinkedIn posts for working professionals across different industries. Please include industry insights, personal experiences, and thought leadership while maintaining a genuine and conversational tone. Please create a post about "{{topic}}" for the industry "{{industry}}". Add emojis to the content when appropriate and write from a personal experience. The content should be between 390 - 400 words long and spaced out that it''s easy for readers to scan through. Please add relevant hashtags to the post and encourage the readers to comment.', '[{"name":"topic","type":"text","label":"Topic","required":true,"placeholder":"Enter topic"},{"name":"industry","type":"text","label":"Industry","required":true,"placeholder":"Enter industry"},{"name":"post_length","type":"text","label":"Post Length","default":"390 - 400 words","required":true,"placeholder":"390 - 400 words"}]'::jsonb, FALSE, TRUE, '2025-11-07T03:59:36.000Z'::timestamp, '2025-11-07T03:59:36.000Z'::timestamp);
