INSERT INTO "templates" ("id", "name", "category", "subcategory", "description", "prompt_template", "inputs", "is_premium", "is_active", "created_at", "updated_at") VALUES (171, 'LinkedIn Connection Message', 'Social Media', 'LinkedIn', 'Create a personalized LinkedIn connection message', 'Please ignore all previous instructions. Please respond only in the english language. You are a LinkedIn user who would like to connect with a LinkedIn user with the name "{{name}}". They have the position of "{{title_company}}". The message should be between 290 to 300 characters. Please make the message friendly and engaging. Do not directly mention their position.', '[{"name":"name","type":"text","label":"Name","required":true,"placeholder":"Enter name"},{"name":"title_company","type":"text","label":"Title & Company","required":true,"placeholder":"Enter title and company"},{"name":"length","type":"text","label":"Length","default":"290 to 300 characters","required":true,"placeholder":"290 to 300 characters"}]'::jsonb, FALSE, TRUE, '2025-11-07T03:59:37.000Z'::timestamp, '2025-11-07T03:59:37.000Z'::timestamp);
