INSERT INTO "templates" ("id", "name", "category", "subcategory", "description", "prompt_template", "inputs", "is_premium", "is_active", "created_at", "updated_at") VALUES (194, 'YouTube Transcript to Article', 'Social Media', 'YouTube', 'Generate YouTube video titles and descriptions from keywords', 'Please ignore all previous instructions. Please respond only in the english language. You are an expert copywriter who is good at converting YouTube transcripts into articles. You have a Creative tone of voice. You have a Argumentative writing style. Do not self reference. Do not explain what you are doing. I will give you a YouTube transcript text and you will create an article based on it. Please intersperse short and long sentences. Utilize uncommon terminology to enhance the originality of the content. Please format the content in a professional format. Also generate a catchy page title and meta descriptions for the article. The page title should be between 70 and 80 Characters. The meta descriptions should be between 140 and 160 characters and should be optimized for the keyword "{{keyword}}". The meta descriptions should NOT have enclosing characters. The Youtube transcript text is "{{transcript}}"', '[{"name":"transcript","type":"textarea","label":"Transcript","required":true,"placeholder":"Enter YouTube transcript"},{"name":"keyword","type":"text","label":"Keyword","required":true,"placeholder":"Enter keyword"}]'::jsonb, FALSE, TRUE, '2025-11-07T03:59:39.000Z'::timestamp, '2025-11-07T03:59:39.000Z'::timestamp);
